import React from "react";

export class Footer extends React.Component {
  render() {
    const socialMediaLinks = [
      {
        cName: "facebook",
        aLink: "http://freewebsitetemplates.com/go/facebook/",
        linkName: "facebook",
      },
      {
        cName: "twitter",
        aLink: "http://freewebsitetemplates.com/go/twitter/",
        linkName: "twitter",
      },
      {
        cName: "googleplus",
        aLink: "http://freewebsitetemplates.com/go/googleplus/",
        linkName: "googleplus",
      },
    ];

    const mediaLinks = socialMediaLinks.map((link) => {
      return (
        <a href={"/" + link.aLink} className={link.cName}>
          {link.linkName}
        </a>
      );
    });

    const footerSection = (
      <div>
        <div className="connect">{mediaLinks}</div>
        <p>Â© 2023 LocalApp. All Rights Reserved.</p>
        <div style={{ textAlign: "center" }} id="wix"></div>
      </div>
    );

    return <div id="footer">{footerSection}</div>;
  }
}
